from types import FloatType, IntType, LongType, InstanceType, ListType, TupleType

from numpy import ndarray, typeDict

_seqTypes = (ListType, TupleType, ndarray)

def isSequence(s):
    return type(s) in _seqTypes

def isThreeSequence(s):
    return type(s) in _seqTypes and len(s) == 3

## Standard numeric types
_numTypes = [FloatType, IntType, LongType]
## Get numeric types from numpy
for i in typeDict.keys():
    if type(i) == type('') and (i.find('int') >= 0 or i.find('float') >= 0):
        _numTypes.append(typeDict[i])
        
def isNumType(val):
    return type(val) in _numTypes

def isNumTypes(lst):
    for li in lst:
        if type(li) not in _numTypes:
            return False
    return True
